function openJpanel(e){var t=jQuery(e),n=t.attr("data-jpanel-side");switch(n){case"right":t.stop(true,false).animate({right:"0px"},900);break;case"left":t.stop(true,false).animate({left:"0px"},900);break;case"top":t.stop(true,false).animate({top:"0px"},900);break;case"bottom":t.stop(true,false).animate({bottom:"0px"},900);break}t.addClass("jpanel_open")}function closeJpanel(e){var t=jQuery(e),n=jQuery(e+" .jpanelContent"),r=t.attr("data-jpanel-side");switch(r){case"right":t.animate({right:(n.width()+11)*-1+"px"},800);break;case"left":t.animate({left:(n.width()+11)*-1+"px"},800);break;case"top":t.animate({top:(n.height()+1)*-1+"px"},800);break;case"bottom":t.animate({bottom:(n.height()+1)*-1+"px"},800);break}t.removeClass("jpanel_open")}function toggleJpanel(e){if(!jQuery(e).hasClass("jpanel_open")){openJpanel(e)}else{closeJpanel(e)}}function clickJpanel(e){jQuery(e+" .jpanelHandle").click(function(){toggleJpanel(e)})}function hoverJpanel(e){jQuery(e+" .jpanelHandle").hover(function(){openJpanel(e)},function(){});jQuery(e).hover(function(){},function(){closeJpanel(e)})}function initjPanelHandle(e){var t=jQuery(e),n=t.attr("data-jpanel-side"),r={},i=jQuery(e).find(".jpanelHandle"),s=i.outerWidth(),o=i.outerHeight();r.position="absolute";switch(n){case"right":r.top="0";r.left=-(s+11)+"px";break;case"left":r.top="0";r.right=-(s+11)+"px";break;case"top":r.left="0";r.bottom=-o+"px";break;case"bottom":r.left="0";r.top=-o+"px";break}i.css(r)}jQuery.fn.textWidth=function(){var e=jQuery(this).html();var t="<span>"+e+"</span>";jQuery(this).html(t);var n=jQuery(this).find("span:first").width();jQuery(this).html(e);return n}