function openJpanel(e){var t=e.split("_");switch(t[2]){case"right":jQuery(e).stop(true,false).animate({right:"0px"},900);break;case"left":jQuery(e).stop(true,false).animate({left:"0px"},900);break;case"top":jQuery(e).stop(true,false).animate({top:"0px"},900);break;case"bottom":jQuery(e).stop(true,false).animate({bottom:"0px"},900);break}jQuery(e+" .jpanelHandle").addClass("jpanel_open")}function closeJpanel(e){var t=e.split("_");switch(t[2]){case"right":jQuery(e).animate({right:(jQuery(e+" .jpanelContent").width()+11)*-1+"px"},800);break;case"left":jQuery(e).animate({left:(jQuery(e+" .jpanelContent").width()+11)*-1+"px"},800);break;case"top":jQuery(e).animate({top:(jQuery(e+" .jpanelContent").height()+1)*-1+"px"},800);break;case"bottom":jQuery(e).animate({bottom:(jQuery(e+" .jpanelContent").height()+1)*-1+"px"},800);break}jQuery(e+" .jpanelHandle").removeClass("jpanel_open")}function clickJpanel(e){jQuery(e+" .jpanelHandle").click(function(){if(!jQuery(e+" .jpanelHandle").hasClass("jpanel_open")){openJpanel(e)}else{closeJpanel(e)}})}function hoverJpanel(e){jQuery(e+" .jpanelHandle").hover(function(){openJpanel(e)},function(){});jQuery(e).hover(function(){},function(){closeJpanel(e)})}function initjPanelHandle(e){var t={},n=e.split("_"),r=jQuery(e).find(".jpanelHandle"),i=r.outerWidth(),s=r.outerHeight();t.position="absolute";switch(n[2]){case"right":t.top="0";t.left=-(i+11)+"px";break;case"left":t.top="0";t.right=-(i+11)+"px";break;case"top":t.left="0";t.bottom=-s+"px";break;case"bottom":t.left="0";t.top=-s+"px";break}r.css(t);console.log(t)}jQuery.fn.textWidth=function(){var e=jQuery(this).html();var t="<span>"+e+"</span>";jQuery(this).html(t);var n=jQuery(this).find("span:first").width();jQuery(this).html(e);return n}